if (msg.payload !== undefined)
{
    //Parse Instruction and send control signals
    instr = msg.payload
    instr = instr.split(" ")

    //Operation signal
    op = instr[0]

    //r/w signal
    if (op == "str")
    {
        rw = "w"
    }
    else
    {
        rw = 'r'
    }


    instr2 = instr[1].split(",")

    //WB reg
    wbreg = instr2[0]

    //reg 1 or immediate
    if (op == "mov" || op == "ld")
    {
        reg1 = "4"
        immed = {payload: instr2[1]}
    }
    else
    {
        reg1 = instr2[1]
        immed = []
    }

    //reg 2 or str val
    if (op == "str" || op == "ld" || op == "mov")
    {
        reg2 = "4"
        straddr = {payload: instr2[0]}
    }
    else
    {
        reg2 = instr2[2]
        straddr = []
    }

    //alu/mem signal
    if (op == "ld" || op == "str")
    {
        alumem = "mem"

        //Don't write back on str
        if (op == "str")
            wbreg = "4"
    }
    else
    {
        alumem = "alu"
    }


    return [{payload: op}, {payload: rw}, {payload: wbreg}, {payload: reg1}, {payload: reg2}, {payload: alumem}, immed, straddr]
}

